CREATE TABLE annonceurs(id_anncrs bigint PRIMARY KEY AUTO_INCREMENT, nom_anncrs varchar(100) CHARACTER SET utf8 NOT null, email_anncrs varchar(150) CHARACTER SET utf8 NOT null, tel_anncrs varchar(20) NOT null);
CREATE TABLE evernement(id_event bigint PRIMARY KEY AUTO_INCREMENT, nom_event varchar(150) CHARACTER SET utf8 NOT null, organisator varchar(100) CHARACTER SET utf8 NOT null, datdeb date NOT null, datefin date NOT null);
CREATE TABLE annonces(id_anncs bigint PRIMARY KEY AUTO_INCREMENT, descrip text CHARACTER SET utf8 NOT null, objectif  varchar(50)  CHARACTER SET utf8 NOT null, url text CHARACTER SET utf8 NOT null,type_url varchar(50) CHARACTER SET utf8 NOT null, type_anncs int not null, duree int not null, step int NOT null, status varchar(20)  CHARACTER SET utf8 NOT null, id_anncrs bigint NOT null,id_event bigint NOT null,FOREIGN KEY(id_anncrs) REFERENCES annonceurs, FOREIGN KEY(id_event) REFERENCES evernement);
CREATE TABLE gagner(id_anncs bigint NOT null, id_event bigint NOT null, ref varchar(10) CHARACTER set utf8 NOT null, PRIMARY KEY(id_anncs,id_event), FOREIGN KEY(id_anncs) REFERENCES annonces, FOREIGN KEY(id_event) REFERENCES evernement);