CREATE TABLE annonceurs(id_anncrs bigint PRIMARY KEY AUTO_INCREMENT, nom_anncrs varchar(100) CHARACTER SET utf8 NOT null, email_anncrs varchar(150) CHARACTER SET utf8 NOT null, tel_anncrs varchar(20) NOT null);
CREATE TABLE evenements(id_event bigint PRIMARY KEY AUTO_INCREMENT, nom_event varchar(150) CHARACTER SET utf8 NOT null, organisator varchar(100) CHARACTER SET utf8 NOT null, datdeb date NOT null, datfin date NOT null);
CREATE TABLE annonces(id_anncs bigint PRIMARY KEY AUTO_INCREMENT, id_anncrs bigint NOT null,id_event bigint NOT null, description text CHARACTER SET utf8 NOT null, objectif  varchar(50)  CHARACTER SET utf8 NOT null, type_med varchar(50) CHARACTER SET utf8 NOT null,type_url varchar(50) CHARACTER SET utf8 null, url_des text CHARACTER SET utf8 NOT null, type_anncs int not null, duree int not null, limite int null, status varchar(20)  CHARACTER SET utf8 NOT null,FOREIGN KEY(id_anncrs) REFERENCES annonceurs, FOREIGN KEY(id_event) REFERENCES evernement);
CREATE TABLE gagner(id_gagner bigint AUTO_INCREMENT NOT null, id_anncs bigint NOT null, id_event bigint NOT null, ref varchar(10) CHARACTER set utf8 NOT null, PRIMARY KEY(id_gagner,id_anncs,id_event), FOREIGN KEY(id_anncs) REFERENCES annonces, FOREIGN KEY(id_event) REFERENCES evernement);
CREATE TABLE media(id_med bigint PRIMARY KEY AUTO_INCREMENT, id_anncs bigint NOT null, url_med text CHARACTER SET utf8 NOT null,FOREIGN KEY(id_anncs) REFERENCES annonces);